var seconds = 0;
var minutes = 0;

function setSeconds() {
    setState('chart.seconds', Math.round(Math.sin(seconds * Math.PI / 180) * 100) / 100, true);
    seconds++;
    if (seconds >= 360) seconds = 0;
}

createState('chart.seconds', 0, function () {
    setSeconds();
    setInterval(setSeconds, 2000);
});

var minutes = 0;

function setMinutes() {
    setState('chart.minutes', Math.round(Math.sin(minutes * Math.PI / 180) * 100) / 100, true);
    minutes++;
    if (minutes >= 360) minutes = 0;
}

createState('chart.minutes', 0, function () {
    setMinutes();
    setInterval(setMinutes, 60000);
});

