createState('values', function() {
    createState('ticks', function() {
        timer = setInterval(createValues, 1000);
    });
})

var i = 0;
function createValues() {
    var ts = new Date().getTime();
    setState('values', {val: i * 10, ts: ts});
    setState('ticks', {val: i, ts: ts});



    i++;
    if (i > 100) {
        clearInterval(timer);
    }
}
